import { getSearchedRepositories } from 'client/services/github';
import { useEffect, useState } from 'react';
import { PaginationProps } from 'semantic-ui-react';

// eslint-disable-next-line @typescript-eslint/no-explicit-any
export const usePagination = <T extends Record<string, any>>(
  data: T[],
  alreadyPaginatedData: T[],
  limit: number,
): {
  paginatedData: T[];
  handleNextPage: (_: React.MouseEvent<HTMLAnchorElement>, props: PaginationProps) => void;
  activePage: number;
  totalPages: number;
  onSearch: (e: any) => voi;
} => {
  const [activePage, setActivePage] = useState(1);
  const [paginatedData, setPaginatedData] = useState<T[]>([]);
  const [keyword, setKeyword] = useState('');
  useEffect(() => {
    if (!keyword) {
      setActivePage(1);
      setPaginatedData(data.slice((activePage - 1) * limit, limit * activePage));
    }
    getSearchedRepositories({ q: keyword, page: activePage });
    setPaginatedData(alreadyPaginatedData);
  }, [activePage, data, keyword]);
  const handleNextPage = (_: React.MouseEvent<HTMLAnchorElement>, props: PaginationProps) => {
    setActivePage(props.activePage as number);
  };

  const onSearch = (e: any) => {
    setKeyword(e.target.value);
  };

  return {
    paginatedData,
    handleNextPage,
    activePage,
    totalPages: Math.ceil(data.length / limit),
    onSearch,
  };
};
