import { getSearchedRepositories } from 'client/actions/github';
import RepositoryCard from 'client/components/RepositoryCard';
import { usePagination } from 'client/hooks/usePagination';
import { RootState, useAppDispatch } from 'client/_redux/app/store';
import { Repository } from 'models/Repository';
import React, { useEffect, useCallback, useState } from 'react';
import { useSelector } from 'react-redux';
import { Card, Input, Pagination } from 'semantic-ui-react';

const Home: React.FC = () => {
  const { repositories, loading } = useSelector((state: RootState) => state.githubReducer);
  const dispatch = useAppDispatch();

  const { handleNextPage, activePage } = usePagination();
  const [keyword, setKeyword] = useState('q');
  const onSearch = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    setKeyword(e.target.value);
  }, []);
  useEffect(() => {
    dispatch(getSearchedRepositories({ q: keyword, page: activePage }));
  }, [activePage, keyword]);
  return (
    <div className="App">
      <h1>Repositories</h1>
      <Input onChange={onSearch} className="py-1" loading={loading} placeholder="Search..." />
      <Card.Group>
        {repositories?.items?.map((rep) => {
          const { name, owner, full_name, description, html_url, id } = rep;
          return (
            <RepositoryCard key={id} {...{ name, owner, full_name, description, html_url }} />
          );
        })}
      </Card.Group>
      <div className="center py-1">
        <Pagination
          onPageChange={handleNextPage}
          defaultActivePage={activePage}
          totalPages={Math.ceil(repositories?.total_count || 30 / 30)}
        />
      </div>
    </div>
  );
};

export default Home;
