import { createSlice } from '@reduxjs/toolkit';
import { getPublicRepositories } from 'client/actions/github';

export type countState = {
  repositories: any[];
  loading: boolean;
};

const initialState: countState = {
  repositories: [],
  loading: false,
};
export const countSlice = createSlice({
  name: 'github',
  initialState,
  reducers: {},
  extraReducers: {
    [getPublicRepositories.pending.type]: (state) => {
      state.loading = true;
    },
    [getPublicRepositories.fulfilled.type]: (state, { payload }: any) => {
      state.count = payload;
    },
    [getPublicRepositories.rejected.type]: (state) => {
      state.loading = false;
    },
  },
});

export const { incrementAction } = countSlice.actions;
export default countSlice.reducer;
