import { Repository } from 'models/Repository';
import React from 'react';
import { Link } from 'react-router-dom';
import { Card, Checkbox, Image } from 'semantic-ui-react';

interface RepositoryCardProps
  extends Pick<
    Repository,
    'name' | 'owner' | 'full_name' | 'description' | 'html_url' | 'markedNumber'
  > {
  onFavorite: (repository: Repository) => void;
}
const RepositoryCard: React.FC<RepositoryCardProps> = ({
  name,
  owner,
  full_name,
  description,
  html_url,
  markedNumber,
  onFavorite,
}) => (
  <Card fluid>
    <Card.Content>
      <Image floated="right" size="mini" src={owner.avatar_url} />
      <Card.Header>{name}</Card.Header>
      <Checkbox label="Favorite" checked={markedNumber !== undefined} />
      <Card.Meta>{full_name}</Card.Meta>
      <Link to={{ pathname: html_url }} target="_blank">
        {html_url}
      </Link>
      <Card.Description>{description}</Card.Description>
    </Card.Content>
  </Card>
);
export default RepositoryCard;
