import { getPublicRepositories, getSearchedRepositories } from 'client/actions/github';
import RepositoryCard from 'client/components/RepositoryCard';
import { usePagination } from 'client/hooks/usePagination';
import { useDebounce } from 'client/hooks/useDebounce';
import { RootState, useAppDispatch } from 'client/_redux/app/store';
import { Repository } from 'models/Repository';
import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { Card, Input, Pagination, Select } from 'semantic-ui-react';
import { sortOptions } from 'client/constants/sortOptions';
import { sortByAlphabetic } from 'client/_redux/reducers/githubReducer';

const Home: React.FC = () => {
  const { repositories, loading } = useSelector((state: RootState) => state.githubReducer);
  const [keyword, setKeyword] = useState('');
  const dispatch = useAppDispatch();
  useEffect(() => {
    dispatch(getPublicRepositories());
  }, []);
  const debounceSearchTerm = useDebounce(keyword, 500);

  useEffect(() => {
    if (!debounceSearchTerm) return;
    dispatch(getSearchedRepositories({ q: debounceSearchTerm }));
  }, [debounceSearchTerm]);

  const { paginatedData, handleNextPage, activePage, totalPages } = usePagination<Repository>(
    repositories,
    30,
  );
  const onSearch = (event: React.ChangeEvent<HTMLInputElement>) => {
    setKeyword(event.target.value);
  };
  const onSort = (event: React.ChangeEvent<HTMLInputElement>) => {
    // dispatch(sortByAlphabetic())
    console.log(event.target.value);
  };
  return (
    <div className="App">
      <h1>Repositories</h1>
      <Input onChange={onSearch} className="py-1" loading={loading} placeholder="Search..." />
      <Select onChange={onSort} placeholder="Select your sort" options={sortOptions} />
      {paginatedData?.map((rep) => {
        const { name, owner, full_name, description, html_url } = rep;
        return (
          <Card.Group>
            <RepositoryCard {...{ name, owner, full_name, description, html_url }} />
          </Card.Group>
        );
      })}
      <div className="center py-1">
        <Pagination
          onPageChange={handleNextPage}
          defaultActivePage={activePage}
          totalPages={totalPages}
        />
      </div>
    </div>
  );
};

export default Home;
