import { useState } from 'hoist-non-react-statics/node_modules/@types/react';
import { Repository } from 'models/Repository';
import React, { useRef, useMemo } from 'react';
import { Link } from 'react-router-dom';
import { Card, Checkbox, Image } from 'semantic-ui-react';

interface RepositoryCardProps
  extends Pick<
    Repository,
    'name' | 'owner' | 'full_name' | 'description' | 'html_url' | 'marked' | 'id'
  > {
  onFavorite: (id: number) => void;
}
const RepositoryCard: React.FC<RepositoryCardProps> = ({
  name,
  owner,
  full_name,
  description,
  id,
  html_url,
  marked,
  onFavorite,
}) => {
  const [selected, setSelected] = useState(marked);
  return (
    <Card fluid>
      <Card.Content>
        <Image floated="right" size="mini" src={owner.avatar_url} />
        <Card.Header>{name}</Card.Header>
        <Checkbox
          ref={ref}
          label="Favorite"
          onClick={(_, data) => {
            onFavorite(id);
            setSelected(data.checked);
          }}
          checked={selected}
        />
        <Card.Meta>{full_name}</Card.Meta>
        <Link to={{ pathname: html_url }} target="_blank">
          {html_url}
        </Link>
        <Card.Description>{description}</Card.Description>
      </Card.Content>
    </Card>
  );
};

export default RepositoryCard;
