import RepositoryCard from 'client/components/RepositoryCard';
import { sortOptions } from 'client/constants/sortOptions';
import React from 'react';
import { Card, Input, Modal, Pagination, Select } from 'semantic-ui-react';
import { useRepositoriesList } from './useRepositoriesList';

const RepositoriesList: React.FC = () => {
  const {
    activePage,
    onSearch,
    handleNextPage,
    totalPages,
    loading,
    onMarkRepository,
    onSort,
    paginatedData,
  } = useRepositoriesList();
  const [open, setOpen] = React.useState(false);
  return (
    <div>
      <div className="space-between py-1">
        <Input onChange={onSearch} loading={loading} placeholder="Search..." />
        <Select onChange={onSort} placeholder="Select your sort" options={sortOptions} />
      </div>
      <Card.Group>
        {paginatedData?.map((rep) => (
          <RepositoryCard key={rep.id} onFavorite={onMarkRepository} repository={rep} />
        ))}
      </Card.Group>
      <div className="center py-1">
        <Pagination
          onPageChange={handleNextPage}
          defaultActivePage={activePage}
          totalPages={totalPages}
        />
      </div>
      <Modal
        onClose={() => setOpen(false)}
        onOpen={() => setOpen(true)}
        open={open}
        trigger={<Button>Show Modal</Button>}
      >
        <Modal.Header>Select a Photo</Modal.Header>
        <Modal.Content image>
          <Image
            size="medium"
            src="https://react.semantic-ui.com/images/avatar/large/rachel.png"
            wrapped
          />
          <Modal.Description>
            <Header>Default Profile Image</Header>
            <p>
              We've found the following gravatar image associated with your e-mail address.
            </p>
            <p>Is it okay to use this photo?</p>
          </Modal.Description>
        </Modal.Content>
        <Modal.Actions>
          <Button color="black" onClick={() => setOpen(false)}>
            Nope
          </Button>
          <Button
            content="Yep, that's me"
            labelPosition="right"
            icon="checkmark"
            onClick={() => setOpen(false)}
            positive
          />
        </Modal.Actions>
      </Modal>
    </div>
  );
};

export default RepositoriesList;
