import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { getSearchedRepositories } from 'client/actions/github';
import { Repository } from 'models/Repository';
import { PaginationProps } from 'semantic-ui-react';
import { PaginatedResponse } from 'types/utils';

export type countState = {
  repositories?: PaginatedResponse<Repository>;
  loading: boolean;
};

const initialState: countState = {
  loading: false,
};
export const countSlice = createSlice({
  name: 'github',
  initialState,
  reducers: {},
  extraReducers: {
    [getSearchedRepositories.pending.type]: (state) => {
      state.loading = true;
    },

    [getSearchedRepositories.fulfilled.type]: (
      state,
      { payload }: PayloadAction<PaginatedResponse<Repository>>,
    ) => {
      state.repositories = payload;
      state.loading = false;
    },
    [getSearchedRepositories.rejected.type]: (state) => {
      state.loading = false;
    },
  },
});

export default countSlice.reducer;
