import { getSearchedRepositories } from 'client/actions/github';
import { useAppDispatch } from 'client/_redux/app/store';
import { useState, useCallback, useEffect } from 'react';
import { PaginationProps } from 'semantic-ui-react';

export const usePagination = (): {
  handleNextPage: (_: React.MouseEvent<HTMLAnchorElement>, props: PaginationProps) => void;
  activePage: number;
  setActivePage: (value: React.SetStateAction<number>) => void;
  onSearch: (e: React.ChangeEvent<HTMLInputElement>) => void;
} => {
  const dispatch = useAppDispatch();

  const [activePage, setActivePage] = useState(1);

  const handleNextPage = (_: React.MouseEvent<HTMLAnchorElement>, props: PaginationProps) => {
    setActivePage(props.activePage as number);
  };

  const [keyword, setKeyword] = useState('q');
  const onSearch = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    setKeyword(e.target.value);
    setActivePage(1);
  }, []);
  useEffect(() => {
    dispatch(getSearchedRepositories({ q: keyword, page: activePage }));
  }, [activePage, keyword]);
  return {
    handleNextPage,
    activePage,
    setActivePage,
    onSearch,
  };
};
